import React from 'react';
import {Channel, useChatContext, Me} from 'stream-chat-react';

import { ChannelInner, CreateChannel, EditChannel} from './';

const ChannelContainer = ({isCreating, setIsCreating, isEditing, setCreateType, setIsEditing, createType,}) => {


    const {channel} = useChatContext();

    if(isCreating) {
        return (
            <div className="channel__container">
                <CreateChannel createType={createType} setIsCreating={setIsCreating}/>
            </div>
        );
    };
    if(isEditing){
        return (
            <div className="channel__container">
                <EditChannel setIsEditing={setIsEditing} />
            </div>
        );
    };

    const EmptyState =() => (
        <div className="channel-empty__container">
            <p className="channel-empty__first">
                Chat begins :)
            </p>
            <p className="channel-empty__second">
                You can sent messages, attachments, links, emojis and more!
            </p>
        </div>
    )

    return (
        <div className="channel__container">
            <Channel
                EmptyStateIndicator={EmptyState}
                Message={(messageProps, i) => <TeamMessage key={i} {...messageProps}/>}
            >
                <ChannelInner setIsEditing={setIsEditing}/>
            </Channel>
        </div>
    );
}

export default ChannelContainer
